CREATE TABLE PRODUCT(ID INTEGER PRIMARY KEY AUTOINCREMENT, REFERENCE_CODE TEXT NOT NULL, NAME TEXT NOT NULL, DESCRIPTION TEXT NOT NULL);
CREATE TABLE PRODUCT_IMAGE(PRODUCT_ID INTEGER NOT NULL, SOURCE_PATH TEXT NOT NULL, FOREIGN KEY(PRODUCT_ID) REFERENCES PRODUCT(ID) ON DELETE CASCADE, PRIMARY KEY (PRODUCT_ID, SOURCE_PATH));
CREATE TABLE PRODUCT_PRICE(PRODUCT_ID INTEGER NOT NULL, PRODUCT_SIZE TEXT NOT NULL, PRODUCT_PRICE REAL NOT NULL, FOREIGN KEY(PRODUCT_ID) REFERENCES PRODUCT(ID) ON DELETE CASCADE, PRIMARY KEY (PRODUCT_ID, PRODUCT_SIZE, PRODUCT_PRICE));
CREATE TABLE PRODUCT_CATEGORY(PRODUCT_ID INTEGER NOT NULL, CATEGORY_ID INTEGER NOT NULL, FOREIGN KEY(PRODUCT_ID) REFERENCES PRODUCT(ID) ON DELETE CASCADE, FOREIGN KEY(CATEGORY_ID) REFERENCES CATEGORY(ID) ON DELETE CASCADE, PRIMARY KEY (PRODUCT_ID, CATEGORY_ID));
CREATE TABLE CATEGORY(ID INTEGER PRIMARY KEY AUTOINCREMENT, NAME TEXT NOT NULL, BACKGROUND_IMAGE TEXT);
CREATE TABLE OFFER(ID INTEGER PRIMARY KEY AUTOINCREMENT, BANNER_TYPE INTEGER NOT NULL, LABEL_TEXT TEXT, BACKGROUND_SOURCE_PATH TEXT NOT NULL, SEARCH_QUERY TEXT);
CREATE TABLE OFFER_TEXT(ID INTEGER PRIMARY KEY AUTOINCREMENT, OFFER_ID INTEGER NOT NULL, OFFER_TEXT TEXT NOT NULL, FOREIGN KEY(OFFER_ID) REFERENCES OFFER(ID) ON DELETE CASCADE);